train_data = []
sentence_count = 0  # Initialize a counter

for response in responses:
    data = json.loads(response)
    sentences = data['sentences']

    for sentence in sentences:
        s1 = sentence['sentence']
        category_dict = sentence['category']

        true_category = None
        for cat, value in category_dict.items():
            if value == 1:
                true_category = cat
                break

        cats_annotation = {}
        for label in ["background", "result", "conclusion"]:
            if true_category and label == true_category:
                cats_annotation[label] = 1.0
            else:
                cats_annotation[label] = 0.0

        train_data.append((s1, {"cats": cats_annotation}))
        sentence_count += 1 # Increment the counter for each added sentence

# After processing all responses and sentences, print the count
print(f"\nTotal number of sentences/examples in train_data: {sentence_count}")



# Load a pre-trained model (en_core_web_sm is fine for this purpose)
nlp = spacy.load("en_core_web_md")

print("nlp model loaded")

# Get or create the textcat_multilabel component
if "textcat_multilabel" not in nlp.pipe_names:
    textcat = nlp.add_pipe("textcat_multilabel")
else:
    textcat = nlp.get_pipe("textcat_multilabel")

