for label in labels:
    textcat.add_label(label)


examples = []
for text, annotations in train_data:
    doc = nlp.make_doc(text) # Create Doc object from text
    examples.append(Example.from_dict(doc, annotations)) # Create Example object


# 5. Train the textcat_multilabel component
n_iter = 50 # Number of training iterations
optimizer = nlp.initialize() # Initialize the model and optimizer
batch_size = 4 # Adjust batch size as needed

print("Training...")
for i in range(n_iter):
    losses = {}
    random.shuffle(examples) # Shuffle examples for each iteration
    batches = minibatch(examples, size=compounding(batch_size, 32.0, 1.001)) # Create batches
    for batch in batches:
        nlp.update(batch, sgd=optimizer, drop=0.2, losses=losses) # Update model
    print(f"Iteration {i+1} - Losses: {losses}")

